function getAtt(el){return el.getAttribute("data-target")}
$(document).ready(function(){$('#sidebar p').each(function(){$(this).next("ul.collapse").toggle()});$('#sidebar ul.collapse > li.sct').each(function(){$(this).siblings('li.sct-sub').hide()});$('#sidebar p').each(function(){$chapter=$(this);$chapter.children('a').on("click",function(e){e.stopPropagation()});$chapter.children('a[data-toggle="collapse"]').on("click",function(e){$(this).children('i').toggleClass('fa-caret-right fa-caret-up');$target=getAtt(this);$($target).toggle();e.stopPropagation()})});$('#sidebar ul.collapse > li.sct').each(function(){$section=$(this);$section.children('a[data-toggle="collapse"]').on("click",function(e){$(this).children('i').toggleClass('fa-caret-right fa-caret-up');$(this).parent().nextUntil('li.sct','li.sct-sub').toggle();e.stopPropagation()})});$('#workspace').click(function(){$('#index').css("margin-left","initial").focus();$('.sidebar-toggle').css("margin-left","initial").blur()});$('#sidebar-checkbox').click(function(){$('.sidebar-toggle').css("margin-left","initial").blur();$('#index').css("margin-left","initial").focus()})})